						<tr id="ccb4a4afc3e1c8ea88e03165865a5833">
							<td class="tb-td" id="15bec584bac32051371c427917d14b23">
								<p id="0fcb8bd39481bfb0b2d8e09c3f4a3982">Enfoque tecnológico</p>
							</td>
							<td class="tb-td" id="747a3294d8602981c4c573adfd6bb3c8">
