						<tr id="a0c1db2fe59b402d371ca7153f7b49c6">
							<td class="tb-td" id="da014a5b4841b1e76857f0f18d2433f4">
								<p id="bf9f303947a9bcccdb6a117a417b1f41">Comunicação bidirecional</p>
							</td>
							<td class="tb-td" id="3dc61130f96f26bdd1c92dc483c6ad95">
